import { Component, OnInit } from '@angular/core';

import { Product } from 'src/app/model/Product';
import { ProductService } from 'src/app/services/product.service';
import { Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent implements OnInit {

  allProducts: Product[] = [];
  products: Product[] = [];

  idProduct!: Product;

  totalP!: number;
  search: string = '';

  constructor( 
    private productServices: ProductService,
    private route: ActivatedRoute) { }


  ngOnInit(): void {
        //inicializado a busca do produtos para exibilos no html
    this.productServices.getProduct().subscribe((item) => {

       const data = item.data;  

      this.products = data;
      this.allProducts = data;
      this.totalP = data.length;

      console.log(data);


    });



  }

  //Pesquisa
  searchs(e: Event): void {
      const target = e.target as HTMLInputElement;
      const value = target.value;

      this.products = this.allProducts.filter((item)=> {
        return item.name.toLowerCase().includes(value);
      });
  }

  async remove(id: any){    

    await this.productServices.removeProduct(id).subscribe();

    console.log('Produto excluído com sucess!');

  }

}
<div class="container">
    <div class="buttonsAction">
        <div class="search">
            <label for="search">Pequisa</label>
            <input type="text" name="search" id="" (input)="searchs($event)">
        </div>
        <div class="new">
            <input type="button" value="+" routerLink="/newProduct">
        </div>
    </div>

    <!-- Tabela -->
    <div class="tableSection">
        <section>
            <div class="tbl-header">
              <table cellpadding="0" cellspacing="0">
                <thead class="thead">
                  <tr>
                    <th>Código</th>
                    <th>Descrição</th>
                    <th>Preço</th>
                    <th>Estoque</th>
                    <th>Ação</th>
                  </tr>
                </thead>
              </table>
            </div>
            <div class="tbl-content">
              <table cellpadding="0" cellspacing="0" class="tableBody">
                <tbody id="tabela-contas" *ngIf="products.length > 0; else noProducts">
                    <tr *ngFor="let product of products" class="tr">
                        <td>{{product.code}}</td>
                        <td>{{product.name}}</td>
                        <td>R$ {{product.price}}</td>
                        <td>UN {{product.inventory ? product.inventory : 0}}</td>                       
                        <td class="action">
                            <input type="button" value="Editar" class="edit" routerLink="/editProduct/{{product._id}}"/>  
                            <input type="button" value="Excluir" class="exclui" (click)="remove(product._id)"/>                         
                        </td>                    
                    </tr>                    
                </tbody>
              </table>              
            </div>
          </section>
    </div>
    <div class="footerProduct">
        <h3 clas>Total de produtos cadastrados: {{totalP}}</h3>
    </div>
    <ng-template #noProducts>
      <h1>Carregando...</h1>
    </ng-template>
</div>
