import { Component, OnInit } from '@angular/core';
import { ɵinitDomAdapter } from '@angular/platform-browser';
import { Router } from '@angular/router';

import { Product } from 'src/app/model/Product';
import { AlertService } from 'src/app/services/alert.service';
import { ProductService } from 'src/app/services/product.service';


@Component({
  selector: 'app-new-product',
  templateUrl: './new-product.component.html',
  styleUrls: ['./new-product.component.css']
})
export class NewProductComponent implements OnInit {

  btnText = 'Cadastrar'

  title = 'Novo produto'

  constructor(
    private router: Router, 
    private productServices: ProductService,
    private alert: AlertService) { }

  ngOnInit(): void {
  }

async createProduct(data: Product){

    // const formData = new FormData();

    // formData.append("code", data.code);
    // formData.append("name", data.name);
    // formData.append("price", data.price);
    // formData.append("inventory", data.inventory);
   try {

    const dados = {
      code: data.code,
      name: data.name,
      price: data.price,
      inventory: data.inventory
    }


    await this.productServices.createProduct(dados).subscribe();

    this.alert.add("Produto cadastrado com sucesso!")

    setTimeout(() => {
      this.router.navigate(['/']);
    }, 2000);

    console.log('Produto cadastrado com sucesso!');

   } catch (error) {

     console.log('Erro ao cadastrar produto.');
   }




  }

}
<app-product-form [btnText]="btnText" [title]="title" (onsubmit)="createProduct($event)"></app-product-form>
